func TestConcurrenctSubgroupNames(t *testing.T) {
	r := MustCompile("(?P<foo>foo)(?P<bar>bar)(?P<baz>baz)")

	wg := &sync.WaitGroup{}
	for range 20 {
		wg.Add(1)
		go func() {
			defer wg.Done()
			if len(r.SubexpNames()) != 4 {
				t.Error("wrong number of subgroup names")
			}
		}()
	}

	wg.Wait()
}
